import React, { Component } from "react"
import { Link } from "react-router-dom"

import axios from "axios"

import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST } from "../config/global_constants"

import BuyShirt from "./BuyShirt"


export default class ShirtBlock extends Component {
    componentDidMount() {
        console.log("All shirt: ",this.props.shirt)// shows all shirt
        this.props.shirt.shirtPhotoFilename.map(photo => {
            return axios.get(`${SERVER_HOST}/shirts/photo/${photo.filename}`)
                .then(res => {
                    document.getElementById(photo._id).src = `data:;base64,${res.data.image}`
                })
                .catch(err => {
                    // do nothing
                })
        })
    }

    prevSlide = () => {
        const slideWidth = document.querySelector('.slides').clientWidth;
        const currentSlideIndex = Math.abs(parseInt(document.querySelector('.slides').style.transform) / slideWidth);
        const totalSlides = this.props.shirt.shirtPhotoFilename.length;
        const nextSlideIndex = (currentSlideIndex === 0) ? totalSlides - 1 : currentSlideIndex - 1;
        const translateValue = -(nextSlideIndex * slideWidth);
        document.querySelector('.slides').style.transform = `translateX(${translateValue}px)`;
    }

    nextSlide = () => {
        const slideWidth = document.querySelector('.slides').clientWidth;
        const currentSlideIndex = Math.abs(parseInt(document.querySelector('.slides').style.transform) / slideWidth);
        const totalSlides = this.props.shirt.shirtPhotoFilename.length;
        const nextSlideIndex = (currentSlideIndex === totalSlides - 1) ? 0 : currentSlideIndex + 1;
        const translateValue = -(nextSlideIndex * slideWidth);
        document.querySelector('.slides').style.transform = `translateX(${translateValue}px)`;
    }

    render() {
        // let soldOrForSale = null
        // if (localStorage.accessLevel <= ACCESS_LEVEL_GUEST) {
        //     if (this.props.shirt.sold !== true) {
        //         soldOrForSale = <BuyShirt shirtID={this.props.shirt._id} price={this.props.shirt.price} />
        //     }
        //     else {
        //         soldOrForSale = "SOLD"
        //     }
        // }


        return (

            <div className="shirt-container">
            <div className="carousel">
                {/* Left arrow */}
                 <div className="arrow prev" onClick={this.prevSlide}></div>
            </div>

            <div className="slides">
            {this.props.shirt.shirtPhotoFilename.map(photo => (
                <img key={photo._id} id={photo._id} src={`data:;base64,${photo.image}`} alt="" />
            ))}
            <div className="arrow next" onClick={this.nextSlide}></div>
        </div>

                {/* <div>{this.props.shirt.colour}</div>

                <div>{this.props.shirt.size}</div>

                <div>{this.props.shirt.description}</div>

                <div>{this.props.shirt.quantity}</div> */}
                <Link to={{
                    pathname: "/SubShirt/" + this.props.shirt._id,
                    state: { shirt: this.props.shirt }
                }}>
                    <div className="shirtPhotos">
                        {this.props.shirt.shirtPhotoFilename.map(photo => <img key={photo._id} id={photo._id} alt="" />)}
                    </div>
                    <h2>{this.props.shirt.name}</h2>
                    <div>{this.props.shirt.price}</div>
                    <div>{this.props.shirt.gender}</div>
                </Link>

                <div className="admin-edit-delete-shirt">
                    {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? <Link className="green-button" to={"/EditTShirt/" + this.props.shirt._id}>Edit</Link> : null}

                    {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? <Link className="red-button" to={"/DeleteTShirt/" + this.props.shirt._id}>Delete</Link> : null}

                    {/* {soldOrForSale} */}
                </div>

            </div>
        )
    }
}
