import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import NavigationBar from "./NavigationBar";
import ShirtBlock from "./ShirtBlock";
import Logout from "./Logout";
import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST } from "../config/global_constants";

export default class Main extends Component {
    constructor(props) {
        super(props);

        this.state = {
            shirts: [],
            searchQuery: ""
        };
    }

    componentDidMount() {
        axios.get(`${SERVER_HOST}/shirts`)
            .then(res => {
                this.setState({ shirts: res.data });
            })
            .catch(err => {
                // Handle error
            });
    }

    handleSearchInputChange = (event) => {
        this.setState({ searchQuery: event.target.value });
    };

    render() {
        const { shirts, searchQuery } = this.state;
        const filteredShirts = shirts.filter(shirt =>
            shirt.name.toLowerCase().includes(searchQuery.toLowerCase())
        );

        return (
            <div>
                <NavigationBar />
                {/* Include your login/logout buttons here */}

                {/* Add the search input field */}
                <div className="search-container">
                    <input
                        type="text"
                        placeholder="Search shirts..."
                        value={searchQuery}
                        onChange={this.handleSearchInputChange}
                    />
                </div>

                <div className="main-container">
                    {/* Map through filtered shirts instead of shirts */}
                    {filteredShirts.map((shirt) => <ShirtBlock key={shirt._id} shirt={shirt} />)}
                </div>

                {/* Include your add new item links here */}
            </div>
        );
    }
}
