    loadShirtPhotos() {
        this.state.allOrders.forEach(order => {
            order.eachItemsInOrder.forEach(item => {
                item.shirtPhotoFilename.forEach(photo => {
                    axios.get(`${SERVER_HOST}/shirts/photo/${photo.filename}`)
                        .then(res => {
