import React, { Component } from "react"
import Form from "react-bootstrap/Form"
import { Redirect, Link } from "react-router-dom"
import axios from "axios"

import LinkInClass from "../components/LinkInClass"

import {SERVER_HOST } from "../config/global_constants"

export default class SubShirt extends Component {
    
    constructor() {
        const { state } = this.props.location;
        this.state = {
            shirt:this.props.shirt,
            name: ``,
            // colour: ``,
            size: ``,
            price: ``,
            description: ``,
            // quantity: ``,
            stock: ``,
            shirtPhotoFilename:null,
            wasSubmittedAtLeastOnce: false
        }
    }

    componentDidMount() {
        this.inputToFocus.focus()

        axios.get(`${SERVER_HOST}/shirts/${this.props.match.params.id}`)
            .then(res => {
                this.setState({
                    name: res.data.name,
                    // colour: res.data.colour,
                    size: res.data.size,
                    price: res.data.price,
                    description: res.data.description,
                    // quantity: res.data.quantity
                    stock: res.data.stock
                })
            })
            .catch(err => {
                // do nothing
            })
    }


    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }




    handleSubmit = (e) => {
        e.preventDefault()

        const shirtObject = {
            name: this.state.name,
            // colour: this.state.colour,
            size: this.state.size,
            price: this.state.price,
            description: this.state.description,
            // quantity: this.state.quantity
            stock: this.state.stock

        }

    }


    render() {
        let errorMessage = "";
        if (this.state.wasSubmittedAtLeastOnce) {
            errorMessage = <div className="error">Error: All fields must be filled in<br /></div>;
        }
        console.log(this.state.shirt);
        return (
            <div className="form-container">


                {errorMessage}

                <Form>
                    <Form.Group controlId="name">
                        <Form.Label>Name</Form.Label>
                        <Form.Control ref={(input) => { this.inputToFocus = input }} type="text" name="name" value={this.state.name} onChange={this.handleChange} />
                    </Form.Group>

                    {/* <Form.Group controlId="colour">
                        <Form.Label>Colour</Form.Label>
                        <Form.Control type="text" name="colour" value={this.state.colour} onChange={this.handleChange} />
                    </Form.Group> */}

                    <Form.Group controlId="size">
                        <Form.Label>Size</Form.Label>
                        <Form.Control as="fieldset">
                            <Form.Check
                                inline
                                type="radio"
                                label="XS"
                                name="size"
                                value="XS"
                                checked={this.state.size === "XS"}
                                onChange={this.handleChange}
                            />
                            <Form.Check
                                inline
                                type="radio"
                                label="S"
                                name="size"
                                value="S"
                                checked={this.state.size === "S"}
                                onChange={this.handleChange}
                            />
                            <Form.Check
                                inline
                                type="radio"
                                label="M"
                                name="size"
                                value="M"
                                checked={this.state.size === "M"}
                                onChange={this.handleChange}
                            />
                            <Form.Check
                                inline
                                type="radio"
                                label="L"
                                name="size"
                                value="L"
                                checked={this.state.size === "L"}
                                onChange={this.handleChange}
                            />
                            <Form.Check
                                inline
                                type="radio"
                                label="XL"
                                name="size"
                                value="XL"
                                checked={this.state.size === "XL"}
                                onChange={this.handleChange}
                            />
                        </Form.Control>
                    </Form.Group>


                    <Form.Group controlId="price">
                        <Form.Label>Price</Form.Label>
                        <Form.Control type="text" name="price" value={this.state.price} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="quantity">
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control type="text" name="quantity" value={this.state.quantity} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="description">
                        <Form.Label>Description</Form.Label>
                        <Form.Control as="textarea" name="description" value={this.state.description} onChange={this.handleChange} />
                    </Form.Group>



                    <LinkInClass value="Update" className="green-button" onClick={this.handleSubmit} />

                    <Link className="red-button" to={"/main"}>Cancel</Link>
                </Form>
            </div>
        )
    }
}
