import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";
import { SANDBOX_CLIENT_ID, SERVER_HOST } from "../config/global_constants";
import PayPalMessage from "./PayPalMessage";
import { PayPalButtons, PayPalScriptProvider } from "@paypal/react-paypal-js";

export default class BuyShirt extends Component {
    constructor(props) {
        super(props);

        this.state = {
            redirectToPayPalMessage: false,
            payPalMessageType: null,
            payPalOrderID: null,
        };
    }

    createOrder = (data, actions) => {
        return actions.order.create({
            purchase_units: [
                {
                    amount: {
                        value: this.props.price.toFixed(2), // Ensure total price is formatted properly
                        currency_code: "EUR",
                    },
                },
            ],
        });
    };

    onApprove = (paymentData) => {
        axios
            .post(
                `${SERVER_HOST}/shirtsales/${paymentData.orderID}/${this.props.shirtID}/${this.props.price}`,
                {},
                { headers: { authorization: localStorage.token } }
            )
            .then((res) => {
                this.setState({
                    payPalMessageType: PayPalMessage.messageType.SUCCESS,
                    payPalOrderID: paymentData.orderID,
                    redirectToPayPalMessage: true,
                });
            })
            .catch((errorData) => {
                this.setState({
                    payPalMessageType: PayPalMessage.messageType.ERROR,
                    redirectToPayPalMessage: true,
                });
            });
    };

    onError = (errorData) => {
        this.setState({
            payPalMessageType: PayPalMessage.messageType.ERROR,
            redirectToPayPalMessage: true,
        });
    };

    onCancel = (cancelData) => {
        this.setState({
            payPalMessageType: PayPalMessage.messageType.CANCEL,
            redirectToPayPalMessage: true,
        });
    };

    render() {
        return (
            <div>
                {this.state.redirectToPayPalMessage ? (
                    <Redirect
                        to={`/PayPalMessage/${this.state.payPalMessageType}/${this.state.payPalOrderID}`}
                    />
                ) : null}

                <PayPalScriptProvider
                    options={{
                        currency: "EUR",
                        "client-id": SANDBOX_CLIENT_ID,
                    }}
                >
                    <PayPalButtons
                        style={{ layout: "horizontal" }}
                        createOrder={this.createOrder}
                        onApprove={this.onApprove}
                        onError={this.onError}
                        onCancel={this.onCancel}
                    />
                </PayPalScriptProvider>
            </div>
        );
    }
}
