import React, { Component } from "react";
import axios from "axios";
import NavigationBar from "./NavigationBar";
import { SERVER_HOST } from "../config/global_constants";

export default class Dashboard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                name: "",
                phone: "",
                address: ""
            }
        };
    }

    componentDidMount() {
        let userEmail = JSON.parse(localStorage.getItem("userEmail") || null);

        axios.get(`${SERVER_HOST}/users/email?email=${userEmail}`)
            .then(res => {
                this.setState({ user: res.data });
            })
            .catch(err => {
                console.error("Error fetching user data:", err);
            });
    }

    handleInputChange = (e) => {
        const { name, value } = e.target;
        this.setState(prevState => ({
            user: {
                ...prevState.user,
                [name]: value
            }
        }));
    };

    handleSubmit = () => {
        // Make API call to save user data
        // For example:
        axios.put(`${SERVER_HOST}/users/update`, this.state.user)
            .then(res => {
                console.log("User data updated successfully:", res.data);
                // Optionally, you can update state or show a success message
            })
            .catch(err => {
                console.error("Error updating user data:", err);
                // Handle error
            });
    };

    render() {
        const { user } = this.state;

        return (
            <div>
                <NavigationBar />
                <h2>Welcome, {user.name}</h2>
                <form>
                    <div>
                        <label>Name:</label>
                        <input type="text" name="name" value={user.name} onChange={this.handleInputChange} />
                    </div>
                    <div>
                        <label>Phone:</label>
                        <input type="text" name="phone" value={user.phone} onChange={this.handleInputChange} />
                    </div>
                    <div>
                        <label>Address:</label>
                        <input type="text" name="address" value={user.address} onChange={this.handleInputChange} />
                    </div>
                    <button type="button" onClick={this.handleSubmit}>Save</button>
                </form>
            </div>
        );
    }
}
