import React, { Component } from "react"
import { Link } from "react-router-dom"

import axios from "axios"

import NavigationBar from "./NavigationBar"

import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN,ACCESS_LEVEL_NORMAL_USER, SERVER_HOST } from "../config/global_constants"


export default class ViewAllUsers extends Component {
    constructor(props) {
        super(props)

        this.state = {
            users:[],

        }
    }


    componentDidMount() {
        if(localStorage.accessLevel > ACCESS_LEVEL_NORMAL_USER){
        axios.get(`${SERVER_HOST}/users`)
        .then(res => {
            this.setState({ users: res.data })
        })
        .catch(err => {
            // do nothing
        })
    }
    }


    render() {
        console.log(this.state.users)
        return (
            <div>
                <NavigationBar/>
                <h2>All Users</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Address</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.users.map(user => (
                            <tr key={user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.phone}</td>
                                <td>{user.address}</td>

                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
    
}
