import React, { Component } from "react";
import { Link } from "react-router-dom";
import Logout from "./Logout";
import axios from "axios";
import NavigationBar from "./NavigationBar";
import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, ACCESS_LEVEL_NORMAL_USER, SERVER_HOST } from "../config/global_constants";

export default class ViewAllUsers extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            searchQuery: "",
            sortFunction: "name",
            phoneFilter: "all"
        };
    }

    componentDidMount() {
        if (localStorage.accessLevel > ACCESS_LEVEL_NORMAL_USER) {
            axios.get(`${SERVER_HOST}/users`)
                .then(res => {
                    this.setState({ users: res.data });
                })
                .catch(err => {
                    // Handle error
                    console.error("Error fetching users:", err);
                });
        }
    }

    handleDelete = (userId) => {
        axios.delete(`${SERVER_HOST}/users/${userId}`)
            .then(res => {
                // Update state after successful deletion
                this.setState(prevState => ({
                    users: prevState.users.filter(user => user._id !== userId)
                }));
            })
            .catch(err => {
                // Handle error
                console.error("Error deleting user:", err);
            });
    };

    handleSearchChange = (event) => {
        this.setState({ searchQuery: event.target.value });
    };

    // handleSortChange = (event) => {
    //     this.setState({ sortFunction: event.target.value });
    // };
    handleSortChange = (event) => {
        const selectedSortFunction = event.target.value;
        this.setState({ sortFunction: selectedSortFunction });
    };

    handlePhoneFilterChange = (value) => {
        this.setState({ phoneFilter: value });
    };

    render() {
        console.log(this.state.users)
        const { users, searchQuery, sortFunction, phoneFilter} = this.state;
        const filteredUsers = users.filter(user =>
            user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.phone.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.address.toLowerCase().includes(searchQuery.toLowerCase())
        );

        let filteredUsersWithPhoneFilter = [...filteredUsers];
        // let filteredUsersWithPhoneFilter = [...sortedUsers];


        if (phoneFilter === "irish") {
            filteredUsersWithPhoneFilter = filteredUsersWithPhoneFilter.filter(user => user.phone.startsWith("353"));
        }

        let sortedUsers = [...filteredUsers];
        if (sortFunction === "name") {
            sortedUsers.sort((a, b) => a.name.localeCompare(b.name));
        } else if (sortFunction === "email") {
            sortedUsers.sort((a, b) => a.email.localeCompare(b.email));
        } else if (sortFunction === "phone") {
            sortedUsers.sort((a, b) => {
                if (!a.phone && !b.phone) return 0;
                if (!a.phone) return 1;
                if (!b.phone) return -1;
                return a.phone.localeCompare(b.phone);
            });
        } else if (sortFunction === "address") {
            sortedUsers.sort((a, b) => {
                if (!a.address && !b.address) return 0;
                if (!a.address) return 1;
                if (!b.address) return -1;
                return a.address.localeCompare(b.address);
            });
        }
        // const sort = {
        //     name: (a, b) => a.name.localeCompare(b.name),
        //     email: (a, b) => a.email.localeCompare(b.email),
        //     phone: (a, b) => {
        //         if (!a.phone && !b.phone) return 0;
        //         if (!a.phone) return 1;
        //         if (!b.phone) return -1;
        //         return a.phone.localeCompare(b.phone);
        //     },
        //     address: (a, b) => {
        //         if (!a.address && !b.address) return 0;
        //         if (!a.address) return 1;
        //         if (!b.address) return -1;
        //         return a.address.localeCompare(b.address);
        //     },
        // };

        // let sortedUsers = [...filteredUsers];
        // sortedUsers.sort(sort[sortFunction]);
        // let filteredUsersWithFilter = [...sortedUsers];

        // let filteredUsersWithPhoneFilter = [...filteredUsers];
        // // let filteredUsersWithPhoneFilter = [...sortedUsers];


        // if (phoneFilter === "irish") {
        //     filteredUsersWithPhoneFilter = filteredUsersWithPhoneFilter.filter(user => user.phone.startsWith("353"));
        // }

        // sortedUsers = [...filteredUsersWithPhoneFilter];

        return (
            <div>
                <NavigationBar/>
                <h2>All Users</h2>
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchQuery}
                    onChange={this.handleSearchChange}
                />

                <select value={sortFunction} onChange={this.handleSortChange}>
                    <option value="name">Sort by Name</option>
                    <option value="email">Sort by Email</option>
                    <option value="phone">Sort by Phone</option>
                    <option value="address">Sort by Address</option>
                </select>

                <div>
                    <label htmlFor="phoneFilter">Filter by Phone:</label>
                    <select id="phoneFilter" value={phoneFilter} onChange={(e) => this.handlePhoneFilterChange(e.target.value)}>
                        <option value="all">All</option>
                        <option value="irish">Irish</option>
                        {/* Add more options for other filters if needed */}
                    </select>
                </div>

                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Address</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {sortedUsers.map(user => (
                            <tr key={user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.phone}</td>
                                <td>{user.address}</td>
                                <td>
                                    <button onClick={() => this.handleDelete(user._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className="logoutButton">
                    <button className="logoutbutton"><Logout /></button>
                </div>
            </div>
        );
    }
}
