import React, { Component } from "react"
import { Link } from "react-router-dom"
import Logout from "./Logout"
import axios from "axios"

import NavigationBar from "./NavigationBar"

import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN,ACCESS_LEVEL_NORMAL_USER, SERVER_HOST } from "../config/global_constants"


export default class ViewAllUsers extends Component {
    constructor(props) {
        super(props)

        this.state = {
            users:[],
            searchQuery: "",

        }
    }


    componentDidMount() {
        if(localStorage.accessLevel > ACCESS_LEVEL_NORMAL_USER){
        axios.get(`${SERVER_HOST}/users`)
        .then(res => {
            this.setState({ users: res.data })
        })
        .catch(err => {
            // do nothing
        })
    }
    }
    handleDelete = (userId) => {
        axios.delete(`${SERVER_HOST}/users/${userId}`)
            .then(res => {
                // Update state after successful deletion
                this.setState(prevState => ({
                    users: prevState.users.filter(user => user._id !== userId)
                }));
            })
            .catch(err => {
                // Handle error
                console.error("Error deleting user:", err);
            });
    };

    handleSearchChange = (event) => {
        this.setState({ searchQuery: event.target.value });
    };

    handleSort = (criteria) => {
        const { users } = this.state;

        // Create a copy of the users array to avoid mutating the state directly
        const sortedUsers = [...users];

        // Sort the users based on the chosen criteria
        switch (criteria) {
            case "name":
                sortedUsers.sort((a, b) => a.name.localeCompare(b.name));
                break;
            case "email":
                sortedUsers.sort((a, b) => a.email.localeCompare(b.email));
                break;
            case "phone":
                sortedUsers.sort((a, b) => a.phone.localeCompare(b.phone));
                break;
            case "address":
                sortedUsers.sort((a, b) => a.address.localeCompare(b.address));
                break;
            default:
                // If no valid criteria is provided, do nothing
                return;
        }

        // Update state with the sorted users
        this.setState({ users: sortedUsers });
    };


    render() {
        console.log(this.state.users)
        const { users, searchQuery } = this.state;
        const filteredUsers = users.filter(user =>
            user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.phone.toLowerCase().includes(searchQuery.toLowerCase()) ||
            user.address.toLowerCase().includes(searchQuery.toLowerCase())
        );
        return (
            <div>
                <NavigationBar/>
                <h2>All Users</h2>
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchQuery}
                    onChange={this.handleSearchChange}
                />

                <select value={sortCriteria} onChange={this.handleSortChange}>
                    <option value="name">Sort by Name</option>
                    <option value="email">Sort by Email</option>
                    <option value="phone">Sort by Phone</option>
                    <option value="address">Sort by Address</option>
                </select>

                <table>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Address</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredUsers.map(user => (
                            <tr key={user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.phone}</td>
                                <td>{user.address}</td>
                                <td>
                                    <button onClick={() => this.handleDelete(user._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className="logoutButton">
                    <button className="logoutbutton"><Logout /></button>
                </div>

            </div>
        );
    }

}
